import{_ as d,c as e,o as i,ag as a}from"./chunks/framework.bCALENAD.js";const k=JSON.parse('{"title":"Linux","description":"","frontmatter":{},"headers":[],"relativePath":"notes/others/linux.md","filePath":"notes/others/linux.md","lastUpdated":1770883502000}'),o={name:"notes/others/linux.md"};function s(l,t,c,r,n,h){return i(),e("div",null,[...t[0]||(t[0]=[a('<h1 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h1><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><h3 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h3><ul><li>开源、免费的操作系统内核。广泛用于服务器、嵌入式设备、云计算等场景。</li></ul><h3 id="发展历史" tabindex="-1">发展历史 <a class="header-anchor" href="#发展历史" aria-label="Permalink to &quot;发展历史&quot;">​</a></h3><ol><li><p><strong>Unix 的诞生</strong></p><ul><li>时间：1969年</li><li>地点：贝尔实验室（Bell Labs）</li><li>起源：“Unix”最初是作为“Multics”项目的替代品而诞生，后来成为现代操作系统的重要基石。</li></ul></li><li><p><strong>关键人物</strong></p><ul><li><p><strong>Andrew S. Tanenbaum（谭宁邦）</strong></p><ul><li>开发了 <strong>Minix</strong> 操作系统（1984年）</li><li>用于教学目的，强调操作系统原理的学习</li><li>Minix 是一个类 Unix 系统，但规模较小，适合学习使用</li></ul></li><li><p><strong>Richard Stallman（斯托曼）</strong></p><ul><li><p>发起 <strong>GNU 项目</strong>（GNU = &quot;GNU&#39;s Not Unix&quot;）</p></li><li><p>目标：创建一个完全自由、开源、功能完整的 Unix-like 操作系统</p></li><li><p>推动开发了许多重要工具：Bash（Shell）、GCC（C语言编译器）、Gawk（文本处理工具）、Emacs（编辑器）</p></li><li><p>提出 <strong>GPL 协议</strong>（通用公共许可协议）</p><ul><li>规则： <ol><li>所有软件必须开源，允许获取和修改源代码。</li><li>修改后的版本也必须以开源方式发布（“传染性”）。</li></ol></li></ul></li><li><p><strong>Linus Torvalds（托沃兹）</strong></p><ul><li>1991年，开发了 <strong>Linux 内核</strong></li><li>原因：对 Minix 不满意，希望拥有更强大、可定制的操作系统</li><li>Linux 内核是基于 Unix 设计哲学，但它是独立开发的</li></ul></li></ul></li></ul><table tabindex="0"><thead><tr><th>人物</th><th>贡献</th><th>关键成果</th></tr></thead><tbody><tr><td><strong>谭宁邦</strong></td><td>教学驱动</td><td>Minix（小型类 Unix 系统）</td></tr><tr><td><strong>斯托曼</strong></td><td>开源理念</td><td>GNU 项目、Bash、GCC、GPL 协议</td></tr><tr><td><strong>托沃兹</strong></td><td>技术实现</td><td>Linux 内核（填补 GNU 缺失的部分）</td></tr></tbody></table></li><li><p><strong>Linux 与 GNU 的结合</strong></p><ul><li>Linux 内核 + GNU 工具链 = 完整的自由操作系统</li><li>因此，“Linux”常被误称为“Linux 系统”，实际上应为“<strong>GNU/Linux 系统</strong>”</li><li>例如：Ubuntu、CentOS、Fedora 都是基于 Linux 内核 + GNU 工具构建的发行版</li></ul></li><li><p><strong>历史时间线</strong></p><ul><li>1969：Unix 诞生（贝尔实验室）</li><li>1984：Minix 出现（谭宁邦）</li><li>1984：GNU 项目启动（斯托曼）</li><li>1991：Linux 内核发布（托沃兹）</li></ul></li></ol><blockquote><p>💡 <strong>最终结果</strong>：<br><strong>Linux 内核 + GNU 工具 = 完整的自由操作系统</strong><br> 这就是今天我们使用的各种 Linux 发行版的基础。</p></blockquote><h3 id="系统组成" tabindex="-1">系统组成 <a class="header-anchor" href="#系统组成" aria-label="Permalink to &quot;系统组成&quot;">​</a></h3><ul><li><strong>内核（Kernel）</strong>：核心，管理硬件与进程。</li><li><strong>Shell</strong>：命令行解释器。</li><li><strong>文件系统</strong>：组织和管理文件。</li><li><strong>应用程序</strong>：用户使用的各种软件。</li></ul><h3 id="发行版分类" tabindex="-1">发行版分类 <a class="header-anchor" href="#发行版分类" aria-label="Permalink to &quot;发行版分类&quot;">​</a></h3><table tabindex="0"><thead><tr><th>发行版家族</th><th>代表发行版</th><th>特点</th><th>适用场景</th></tr></thead><tbody><tr><td>RHEL 系</td><td>RHEL、Rocky、AlmaLinux</td><td>企业级稳定，长期支持</td><td>服务器、生产环境</td></tr><tr><td></td><td>CentOS Stream</td><td>RHEL 上游（滚动预览版）</td><td>开发/测试</td></tr><tr><td></td><td>Fedora</td><td>新技术试验田</td><td>开发者、尝鲜</td></tr><tr><td>Debian 系</td><td>Debian</td><td>稳定、自由软件</td><td>通用服务器</td></tr><tr><td></td><td>Ubuntu</td><td>用户友好，生态完善</td><td>桌面、云、开发</td></tr><tr><td></td><td>Kali Linux</td><td>预装安全工具</td><td>渗透测试</td></tr><tr><td>SUSE 系</td><td>openSUSE / SLES</td><td>强大管理工具（YaST）</td><td>企业、HPC</td></tr><tr><td>国产系</td><td>麒麟、红旗</td><td>国产化替代</td><td>政企、信创</td></tr></tbody></table><h2 id="常用操作命令" tabindex="-1">常用操作命令 <a class="header-anchor" href="#常用操作命令" aria-label="Permalink to &quot;常用操作命令&quot;">​</a></h2><h3 id="文件和目录操作" tabindex="-1">文件和目录操作 <a class="header-anchor" href="#文件和目录操作" aria-label="Permalink to &quot;文件和目录操作&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命令</th><th>功能说明与常用参数</th></tr></thead><tbody><tr><td><code>ls</code></td><td>列出目录内容<br>• <code>-l</code>：长格式（显示权限、所有者、大小、时间等）<br>• <code>-a</code>：显示隐藏文件（以 <code>.</code> 开头）<br>• <code>-h</code>：配合 <code>-l</code> 以易读单位（KB/MB）显示大小<br>• <code>-t</code>：按修改时间排序（最新在前）</td></tr><tr><td><code>cd</code></td><td>切换当前工作目录<br>• <code>cd ~</code> 或 <code>cd</code>：回到当前用户主目录<br>• <code>cd ..</code>：返回上一级目录<br>• <code>cd -</code>：切换到上一个工作目录<br>• <code>cd /path/to/dir</code>：进入指定目录</td></tr><tr><td><code>pwd</code></td><td>显示当前完整路径（Print Working Directory）<br>• <code>-P</code>：显示实际物理路径（不包含符号链接）</td></tr><tr><td><code>mkdir</code></td><td>创建目录<br>• <code>-p</code>：递归创建多层目录（如 <code>mkdir -p a/b/c</code>）<br>• <code>-v</code>：显示创建过程（verbose）<br>• 示例：<code>mkdir -pv project/{src,bin,docs}</code></td></tr><tr><td><code>touch</code></td><td>创建空文件或更新文件的时间戳（访问/修改时间）<br>• 可一次创建多个文件：<code>touch file1 file2</code></td></tr><tr><td><code>cp</code></td><td>复制文件或目录<br>• <code>-r</code> 或 <code>-R</code>：递归复制整个目录<br>• <code>-i</code>：覆盖前提示确认（交互式）<br>• <code>-v</code>：显示复制过程<br>• 示例：<code>cp -rv /etc/nginx ./backup/</code></td></tr><tr><td><code>mv</code></td><td>移动文件/目录 或 重命名<br>• <code>-i</code>：覆盖前提示<br>• <code>-v</code>：显示操作过程<br>• 示例：<br> 重命名：<code>mv old.txt new.txt</code><br> 移动：<code>mv *.log /var/log/</code></td></tr><tr><td><code>rm</code></td><td>删除文件或目录<br>• <code>-r</code>：递归删除目录及其内容<br>• <code>-f</code>：强制删除，不提示（慎用！）<br>• <code>-i</code>：删除前确认（安全模式）<br>⚠️ 警告：<code>rm -rf /</code> 会删除整个系统！</td></tr><tr><td><code>vi</code> / <code>vim</code></td><td>强大的文本编辑器<br>• 基本退出命令：<br> <code>:q</code> — 退出（未修改时）<br> <code>:q!</code> — 强制退出，放弃修改<br> <code>:wq</code> 或 <code>:x</code> — 保存并退出<br> <code>:w filename</code> — 另存为<br>• 编辑模式：按 <code>i</code> 进入插入模式，按 <code>Esc</code> 返回命令模式</td></tr><tr><td><code>cat</code></td><td>查看小文件内容（concatenate）<br>• <code>cat file1 file2</code>：合并输出多个文件</td></tr></tbody></table><h3 id="查询与辅助" tabindex="-1">查询与辅助 <a class="header-anchor" href="#查询与辅助" aria-label="Permalink to &quot;查询与辅助&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命令</th><th>说明</th><th>推荐场景</th></tr></thead><tbody><tr><td><code>command --help</code></td><td>显示命令的简易帮助信息（短格式）<br>例如：<code>cat --help</code></td><td>快速查看常用参数，适合初学者</td></tr><tr><td><code>man command</code></td><td>查看命令的 手册页（manual page），包含详细说明、选项、示例等<br>例如：<code>man cat</code></td><td>最常用、最全面的帮助方式</td></tr><tr><td><code>info command</code></td><td>提供比 <code>man</code> 更详细的文档，支持超链接和导航<br>例如：<code>info cat</code></td><td>学习复杂工具（如 <code>gcc</code>、<code>gdb</code>）时推荐使用</td></tr><tr><td><code>help command</code></td><td>仅适用于 shell 内置命令（如 <code>cd</code>, <code>echo</code>, <code>alias</code> 等）<br>例如：<code>help cd</code></td><td>查询 Bash 内置命令时使用</td></tr></tbody></table><h3 id="系统监控与进程管理" tabindex="-1">系统监控与进程管理 <a class="header-anchor" href="#系统监控与进程管理" aria-label="Permalink to &quot;系统监控与进程管理&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命令</th><th>类型</th><th>主要功能</th><th>特点</th><th>典型用法与说明</th></tr></thead><tbody><tr><td><code>ps</code></td><td>进程快照</td><td>显示当前进程的静态快照</td><td>一次性输出，适合脚本</td><td>• <code>ps aux</code>：查看所有用户的所有进程（最常用）<br>• <code>ps -ef</code>：显示完整格式，包含父进程 ID（PPID）<br>• <code>ps aux | grep nginx</code>：查找特定进程</td></tr><tr><td><code>top</code></td><td>实时监控</td><td>动态显示 CPU、内存、进程排名</td><td>交互式，实时刷新（默认 3 秒）</td><td>• 直接输入 <code>top</code> 进入交互界面<br>• 按 <code>P</code> 按 CPU 排序，<code>M</code> 按内存排序<br>• 按 <code>q</code> 退出，<code>k</code> 可直接 kill 进程</td></tr><tr><td><code>kill</code></td><td>进程控制</td><td>向进程发送信号（如终止）</td><td>默认发送 <code>SIGTERM</code>（15）</td><td>• <code>kill 1234</code>：优雅终止 PID 为 1234 的进程<br>• <code>kill -9 1234</code>：强制终止（<code>SIGKILL</code>，慎用）</td></tr><tr><td><code>df</code></td><td>磁盘监控</td><td>显示磁盘分区使用情况</td><td>查看存储空间</td><td>• <code>df -h</code>：以人类可读单位（GB/MB）显示</td></tr><tr><td><code>du</code></td><td>磁盘使用分析</td><td>显示目录/文件占用空间</td><td>定位大文件或目录</td><td>• <code>du -sh /var/log</code>：汇总显示 <code>/var/log</code> 总大小<br>• <code>du -h --max-depth=1 /home</code>：查看 <code>/home</code> 下各子目录大小<br>• <code>du -ah /path | sort -rh | head -n 10</code>：找出最大的 10 个文件/目录</td></tr></tbody></table><h3 id="进阶实用命令" tabindex="-1">进阶实用命令 <a class="header-anchor" href="#进阶实用命令" aria-label="Permalink to &quot;进阶实用命令&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命令</th><th>功能说明</th></tr></thead><tbody><tr><td>wc</td><td>统计文件的行数、单词数、字节数（<code>-l</code>, <code>-w</code>, <code>-c</code>）</td></tr><tr><td>sort</td><td>对文件内容按行排序（常用于配合 <code>uniq</code>）</td></tr><tr><td>uniq</td><td>去除连续重复的行；加 <code>-c</code> 可统计重复次数<br>⚠️ 注意：必须先排序才能完全去重（通常与 <code>sort</code> 联用）</td></tr><tr><td>dos2unix</td><td>转换 Windows（DOS）格式文本为 Unix 格式（处理换行符 <code>\\r\\n</code> → <code>\\n</code>）</td></tr><tr><td>diff / vimdiff</td><td>比较两个文件差异；<code>vimdiff</code> 提供可视化对比</td></tr><tr><td>which / whereis</td><td>查询命令的可执行路径（<code>which</code>）或相关文件位置（<code>whereis</code>）</td></tr><tr><td>date</td><td>查看或设置系统时间</td></tr><tr><td>alias</td><td>查看或设置简短别名，提升效率。示例：<code>alias ll=&#39;ls -l&#39;</code></td></tr><tr><td>tail</td><td>查看文件末尾内容（10 行）<br>• <code>tail -n 20 file.log</code><br>• <code>tail -f file.log</code>：实时监控</td></tr></tbody></table><h3 id="打包" tabindex="-1">打包 <a class="header-anchor" href="#打包" aria-label="Permalink to &quot;打包&quot;">​</a></h3><table tabindex="0"><thead><tr><th>tar命令参数</th><th>含义</th></tr></thead><tbody><tr><td><code>-c</code></td><td>create：创建新的归档文件</td></tr><tr><td><code>-x</code></td><td>extract：从归档中解压文件</td></tr><tr><td><code>-t</code></td><td>list：列出归档中的内容（不解压）</td></tr><tr><td><code>-f</code></td><td>file：指定归档文件名（必须放在最后，后面紧跟文件名）</td></tr><tr><td><code>-v</code></td><td>verbose：显示详细过程（可选，方便查看进度）</td></tr><tr><td><code>-z</code></td><td>用 gzip 压缩或解压（对应 <code>.tar.gz</code> 或 <code>.tgz</code>）</td></tr><tr><td><code>-j</code></td><td>用 bzip2 压缩或解压（对应 <code>.tar.bz2</code>）</td></tr><tr><td><code>-J</code></td><td>用 xz 压缩或解压（对应 <code>.tar.xz</code>）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>格式</th><th>打包压缩命令</th><th>解压命令</th></tr></thead><tbody><tr><td><code>.tar</code></td><td><code>tar -cvf file.tar dir/</code></td><td><code>tar -xvf file.tar</code></td></tr><tr><td><code>.tar.gz</code> / <code>.tgz</code></td><td><code>tar -czvf file.tar.gz dir/</code></td><td><code>tar -xzvf file.tar.gz</code></td></tr><tr><td><code>.tar.bz2</code></td><td><code>tar -cjvf file.tar.bz2 dir/</code></td><td><code>tar -xjvf file.tar.bz2</code></td></tr><tr><td><code>.tar.xz</code></td><td><code>tar -cJvf file.tar.xz dir/</code></td><td><code>tar -xJvf file.tar.xz</code></td></tr></tbody></table><h3 id="四剑客-grep-sed-awk-find" tabindex="-1">四剑客 grep/sed/awk/find <a class="header-anchor" href="#四剑客-grep-sed-awk-find" aria-label="Permalink to &quot;四剑客 grep/sed/awk/find&quot;">​</a></h3><h4 id="_1-grep-——-文本搜索过滤器" tabindex="-1">1. <code>grep</code> —— 文本搜索过滤器 <a class="header-anchor" href="#_1-grep-——-文本搜索过滤器" aria-label="Permalink to &quot;1. `grep` —— 文本搜索过滤器&quot;">​</a></h4><p>主要用途：在文件或输入流中按行匹配正则表达式，快速筛选内容。<br> 核心特点：高效、支持正则、常用于日志分析。</p><p><strong>常用参数</strong>：</p><ul><li><code>-i</code>：忽略大小写</li><li><code>-v</code>：反向匹配（显示不匹配的行）</li><li><code>-r</code> 或 <code>-R</code>：递归搜索子目录</li><li><code>-n</code>：显示匹配行的行号</li><li><code>-l</code>：只列出包含匹配内容的文件名</li></ul><div class="info custom-block"><p class="custom-block-title">典型示例</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 在 /var/log 及其子目录中递归查找包含 &quot;error&quot; 的行</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">grep</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -r</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;error&quot;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /var/log</span></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 过滤掉以 # 开头的注释行，仅显示有效配置</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">grep</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -v</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;^#&quot;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> config.conf</span></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 在当前目录所有 .py 文件中搜索 &quot;TODO&quot;，并显示所在行号</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">grep</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;TODO&quot;</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> *</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">.py</span></span></code></pre></div></div><h4 id="_2-sed-——-流式文本编辑器" tabindex="-1">2. <code>sed</code> —— 流式文本编辑器 <a class="header-anchor" href="#_2-sed-——-流式文本编辑器" aria-label="Permalink to &quot;2. `sed` —— 流式文本编辑器&quot;">​</a></h4><p>主要用途：对文本进行非交互式替换、删除、插入等操作，适合脚本自动化。<br> 核心特点：逐行处理、支持正则替换、可直接修改文件。</p><p><strong>常用参数</strong>：</p><ul><li><code>-i</code>：直接修改原文件（慎用！）</li><li><code>-n</code>：抑制默认输出（需配合 <code>p</code> 打印）</li><li><code>s/old/new/g</code>：全局替换（<code>g</code> 表示一行内全部替换）</li></ul><div class="info custom-block"><p class="custom-block-title">典型示例</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 将 file 中所有 &quot;foo&quot; 替换为 &quot;bar&quot;，输出到终端（不改原文件）</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sed</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;s/foo/bar/g&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> file</span></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 直接删除 file 的第一行（原文件被修改）</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sed</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -i</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;1d&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> file</span></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 仅打印 log 中包含 &quot;ERROR&quot; 的行（`-n` 关闭自动输出，`p` 显式打印匹配行）</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sed</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;/ERROR/p&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> log</span></span></code></pre></div></div><h4 id="_3-awk-——-结构化文本处理器" tabindex="-1">3. <code>awk</code> —— 结构化文本处理器 <a class="header-anchor" href="#_3-awk-——-结构化文本处理器" aria-label="Permalink to &quot;3. `awk` —— 结构化文本处理器&quot;">​</a></h4><p>主要用途：按列处理数据（如日志、CSV），支持条件判断、计算和格式化输出。<br> 核心特点：类似轻量级编程语言，适合生成报表或提取字段。</p><p><strong>常用参数/变量</strong>：</p><ul><li><code>-F&#39;,&#39;</code>：指定字段分隔符（如逗号）</li><li><code>$1, $2...</code>：表示第1、2…列</li><li><code>NR</code>：当前行号；<code>NF</code>：当前行字段数</li><li><code>BEGIN{}</code> / <code>END{}</code>：预处理和后处理块</li></ul><div class="info custom-block"><p class="custom-block-title">典型示例</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 打印每行的第一个字段（如 IP 地址或时间戳）</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">awk</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;{print $1}&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> file</span></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 以逗号分隔，筛选第二列数值大于 100 的行（适用于 CSV 数据分析）</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">awk</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -F</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;,&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;$2 &gt; 100&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> data.csv</span></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 输出文件前 5 行（NR 是行号）</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">awk</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;NR&lt;=5&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> file</span></span></code></pre></div></div><h4 id="_4-find-——-文件系统查找工具" tabindex="-1">4. <code>find</code> —— 文件系统查找工具 <a class="header-anchor" href="#_4-find-——-文件系统查找工具" aria-label="Permalink to &quot;4. `find` —— 文件系统查找工具&quot;">​</a></h4><p>主要用途：根据名称、类型、时间、大小等条件查找文件或目录。 核心特点：功能强大、支持复杂组合条件、可联动其他命令执行操作。</p><p><strong>常用参数</strong>：</p><ul><li><code>-name &quot;*.txt&quot;</code>：按文件名匹配（支持通配符）</li><li><code>-type f</code>（文件）或 <code>-type d</code>（目录）</li><li><code>-mtime +7</code>：7 天前修改过的文件</li><li><code>-size +10M</code>：大于 10MB 的文件</li><li><code>-exec command {} +</code>：对找到的文件批量执行命令</li></ul><div class="info custom-block"><p class="custom-block-title">典型示例</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 在当前目录及子目录中查找所有 .log 文件</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">find</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> .</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -name</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;*.log&quot;</span></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 删除 /tmp 中 7 天前 修改过的文件（⚠️ 谨慎使用 -delete！）</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">find</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /tmp</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -mtime</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> +7</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -delete</span></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 找到所有普通文件，并统一设置权限为 644（+ 表示高效批量传参）</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">find</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> .</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -type</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> f</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -exec</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> chmod</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 644</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> {}</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> +</span></span></code></pre></div></div><blockquote><p>💡 <strong>组合技巧</strong>： 这四个命令经常串联使用，例如：<br><code>find . -name &quot;*.log&quot; -print0 | xargs -0 grep &quot;ERROR&quot; | awk &#39;{print $1, $4}&#39;</code><br> 实现：查找日志 → 筛选错误 → 提取关键字段。</p></blockquote><h3 id="网络" tabindex="-1">网络 <a class="header-anchor" href="#网络" aria-label="Permalink to &quot;网络&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命令</th><th>用途</th></tr></thead><tbody><tr><td><code>ping</code></td><td>测试网络连通性</td></tr><tr><td><code>curl</code></td><td>测试 HTTP 接口</td></tr><tr><td><code>dig</code> / <code>nslookup</code></td><td>DNS 查询</td></tr></tbody></table><h3 id="权限与用户管理" tabindex="-1">权限与用户管理 <a class="header-anchor" href="#权限与用户管理" aria-label="Permalink to &quot;权限与用户管理&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命令</th><th>用途说明</th><th>常用示例</th></tr></thead><tbody><tr><td><code>chmod</code></td><td>修改文件或目录的权限（读 r、写 w、执行 x）</td><td><code>chmod 755 script.sh</code><br>→ 所有者可读写执行，组和其他人可读执行<br><code>chmod +x file</code><br>→ 给文件添加执行权限</td></tr><tr><td><code>chown</code></td><td>修改文件或目录的所有者（可同时改所属组）</td><td><code>chown alice file.txt</code><br>→ 将文件所有者改为 <code>alice</code><br><code>chown alice:dev file.txt</code><br>→ 所有者改为 <code>alice</code>，所属组改为 <code>dev</code></td></tr><tr><td><code>chgrp</code></td><td>修改文件或目录的所属组</td><td><code>chgrp developers project/</code><br>→ 将 <code>project/</code> 目录的所属组改为 <code>developers</code></td></tr><tr><td><code>sudo</code></td><td>以超级用户（root）或其他用户身份执行命令</td><td><code>sudo systemctl restart nginx</code><br>→ 以 root 权限重启服务<br><code>sudo -u bob whoami</code><br>→ 以用户 <code>bob</code> 身份执行 <code>whoami</code></td></tr><tr><td><code>su</code></td><td>切换到其他用户（默认切换到 root）</td><td><code>su -</code><br>→ 切换到 root 并加载其环境<br><code>su - alice</code><br>→ 切换到用户 <code>alice</code></td></tr></tbody></table><h2 id="附录" tabindex="-1">附录 <a class="header-anchor" href="#附录" aria-label="Permalink to &quot;附录&quot;">​</a></h2><h3 id="附1-和-对比" tabindex="-1">附1：<code>$()</code> 和 <code>``</code> 对比 <a class="header-anchor" href="#附1-和-对比" aria-label="Permalink to &quot;附1：`$()` 和 ` `` ` 对比&quot;">​</a></h3><table tabindex="0"><thead><tr><th>特性</th><th><code>$()</code></th><th><code>``</code></th></tr></thead><tbody><tr><td>可读性</td><td>高（括号匹配清晰）</td><td>低（反引号易与单引号混淆）</td></tr><tr><td>嵌套支持</td><td>直接嵌套：<code> $ (cmd1 $ (cmd2))</code></td><td>需转义</td></tr><tr><td>转义处理</td><td>简单（内部双引号无需特殊处理）</td><td>复杂（内部反引号需转义）</td></tr><tr><td>POSIX 标准</td><td>✅ 推荐</td><td>⚠️ 兼容但过时</td></tr></tbody></table>',52)])])}const b=d(o,[["render",s]]);export{k as __pageData,b as default};
